
public class Swapping1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int me=20;
int sis=30;

System.out.println("my age is :" +me);
System.out.println("my sis age is:"+sis);

System.out.println("total age is:"+(me+sis));


int temp=me;
me=sis;
sis=temp;
System.out.println("my age is :"+me);
System.out.println("my sis age is :"+sis);
	}

}
