
public class While_loop4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
				int num =0;
			      while(num<=100)
			      {
			          System.out.println("Value of variable is: "+num);
			          if (num==5)
			          {
				             break;
				          }
			          num++;
			      }
			      System.out.println("Out of while-loop");
			  }
			
	

}
